import React, { useEffect } from 'react';

function Header() {
  useEffect(() => {
    const botao = document.getElementsByClassName("botao")[0];

    botao.addEventListener("click", function(){
      const title = document.getElementsByClassName("title")[0];

      if  (title.textContent === "Dark mode OFF") {
        title.textContent = "Dark mode ON";
        document.body.style.backgroundColor = '#000';
        document.getElementsByClassName("botao")[0].style.backgroundColor = '#fff';
        document.getElementsByClassName("botao")[0].style.color = '#000';
        document.getElementsByClassName("title")[0].style.color = '#fff';
      } else {
        title.textContent = "Dark mode OFF";
        document.body.style.backgroundColor = '#fff';
        document.getElementsByClassName("botao")[0].style.backgroundColor = '#000';
        document.getElementsByClassName("botao")[0].style.color = '#fff';
        document.getElementsByClassName("title")[0].style.color = '#000';
      }
    });

    botao.addEventListener("mouseover", function() {
      document.getElementsByClassName("botao")[0].style.backgroundColor = '#a9b9c1';
      document.getElementsByClassName("botao")[0].style.color = '#000';
    });

    botao.addEventListener("mouseout", function() {
      const title = document.getElementsByClassName("title")[0];
      if (title.textContent === "Dark mode OFF") {
        document.getElementsByClassName("botao")[0].style.backgroundColor = '#000';
        document.getElementsByClassName("botao")[0].style.color = '#fff';
      } else {
        document.getElementsByClassName("botao")[0].style.backgroundColor = '#fff';
        document.getElementsByClassName("botao")[0].style.color = '#000';
      }
    });
  }, []);

  return (
    <header className="bg-blue-600 text-white p-4 flex justify-between items-center">
      <button className="botao bg-black text-white p-2 rounded">Dark mode OFF</button>
      <h1 className="title">Welcome to Dylan's Website</h1>
    </header>
  );
}

export default Header;
