import React, { useState } from 'react';

const ProjectDropdown = ({ pastProjects, ongoingProjects }) => {
  const [activePastIndex, setActivePastIndex] = useState(null);
  const [activeOngoingIndex, setActiveOngoingIndex] = useState(null);

  const togglePastDropdown = (index) => {
    setActivePastIndex(activePastIndex === index ? null : index);
  };

  const toggleOngoingDropdown = (index) => {
    setActiveOngoingIndex(activeOngoingIndex === index ? null : index);
  };

  return (
    <div className="max-w-4xl mx-auto mt-10 p-4">
      <div className="space-y-4">
        <h1 className="text-2xl font-semibold">Past Projects</h1>
        {pastProjects.map((project, index) => (
          <div key={index} className="overflow-hidden">
            <button
              onClick={() => togglePastDropdown(index)}
              className="w-full flex justify-between items-center p-4 transition duration-200 focus:outline-none text-gray-900 dark:text-white"
            >
              <span className="text-lg font-semibold">{project.title}</span>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className={`w-5 h-5 transition-transform duration-200 ${
                  activePastIndex === index ? 'rotate-180' : ''
                }`}
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M19 9l-7 7-7-7"
                />
              </svg>
            </button>
            <div
              className={`transition-all duration-500 ease-in-out transform ${
                activePastIndex === index ? 'max-h-96 scale-y-100 opacity-100' : 'max-h-0 scale-y-0 opacity-0'
              } origin-top bg-gray-100 dark:bg-gray-700`}
              style={{ overflow: 'hidden' }}
            >
              <div className="p-4">
                <p className="text-gray-900 dark:text-gray-300">{project.description}</p>
                {project.link && (
                  <a
                    href={project.link}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="mt-2 inline-block text-blue-500 dark:text-blue-300 hover:text-blue-700 dark:hover:text-blue-500 transition duration-200"
                  >
                    View Project
                  </a>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
      <div className="space-y-4 mt-10">
        <h1 className="text-2xl font-semibold">On-Going Projects</h1>
        {ongoingProjects.map((project, index) => (
          <div key={index} className="overflow-hidden">
            <button
              onClick={() => toggleOngoingDropdown(index)}
              className="w-full flex justify-between items-center p-4 transition duration-200 focus:outline-none text-gray-900 dark:text-white"
            >
              <span className="text-lg font-semibold">{project.title}</span>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className={`w-5 h-5 transition-transform duration-200 ${
                  activeOngoingIndex === index ? 'rotate-180' : ''
                }`}
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M19 9l-7 7-7-7"
                />
              </svg>
            </button>
            <div
              className={`transition-all duration-500 ease-in-out transform ${
                activeOngoingIndex === index ? 'max-h-96 scale-y-100 opacity-100' : 'max-h-0 scale-y-0 opacity-0'
              } origin-top bg-gray-100 dark:bg-gray-700`}
              style={{ overflow: 'hidden' }}
            >
              <div className="p-4">
                <p className="text-gray-900 dark:text-gray-300">{project.description}</p>
                {project.link && (
                  <a
                    href={project.link}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="mt-2 inline-block text-blue-500 dark:text-blue-300 hover:text-blue-700 dark:hover:text-blue-500 transition duration-200"
                  >
                    View Project
                  </a>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProjectDropdown;
