import React, { useEffect } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';

function App() {

  let text = 'Welcome - check out my work below.';

  const typingEffect = (text) => {
    const textArray = text.split('');
    const welcomeTextElement = document.getElementById('welcome-text');
    if (welcomeTextElement) {
      welcomeTextElement.innerHTML = '';
      textArray.forEach((letter, i) => {
        setTimeout(() => {
          welcomeTextElement.innerHTML += letter;
        }, 100 * i);
      });
    }
  };

  useEffect(() => {
    typingEffect(text);
    text = '';

    const dropdownButton = document.getElementById('dropdownHoverButton');
    const dropdownMenu = document.getElementById('dropdownHover');

    const showDropdown = () => {
      dropdownMenu.classList.remove('hidden');
    };

    const hideDropdown = () => {
      dropdownMenu.classList.add('hidden');
    };

    dropdownButton.addEventListener('mouseover', showDropdown);
    dropdownButton.addEventListener('mouseout', hideDropdown);
    dropdownMenu.addEventListener('mouseover', showDropdown);
    dropdownMenu.addEventListener('mouseout', hideDropdown);

    return () => {
      dropdownButton.removeEventListener('mouseover', showDropdown);
      dropdownButton.removeEventListener('mouseout', hideDropdown);
      dropdownMenu.removeEventListener('mouseover', showDropdown);
      dropdownMenu.removeEventListener('mouseout', hideDropdown);
    };
  }, []);

  console.log('App component rendered');
  return (
    <div className="min-h-screen flex flex-col">
      <Header />
      <main className="p-4">
        <p id="welcome-text" className="text-center"></p>

        <button id="dropdownHoverButton" data-dropdown-toggle="dropdownHover" data-dropdown-trigger="hover" className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center inline-flex items-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800" type="button">
          Dropdown hover 
          <svg className="w-2.5 h-2.5 ms-3" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 10 6">
            <path stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="m1 1 4 4 4-4"/>
          </svg>
        </button>

        <div id="dropdownHover" className="z-10 hidden bg-white divide-y divide-gray-100 rounded-lg shadow w-44 dark:bg-gray-700">
          <ul className="py-2 text-sm text-gray-700 dark:text-gray-200" aria-labelledby="dropdownHoverButton">
            <li>
              <a href="#" className="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">Dashboard</a>
            </li>
            <li>
              <a href="#" className="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">Settings</a>
            </li>
            <li>
              <a href="#" className="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">Earnings</a>
            </li>
            <li>
              <a href="#" className="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">Sign out</a>
            </li>
          </ul>
        </div>

      </main>
      <Footer />
    </div>
  );
}

export default App;
