import React, { useState } from 'react';
import '../global.css';

const Accordion = () => {
  const [activeTab, setActiveTab] = useState('ai-assistant');

  const renderContent = () => {
    switch (activeTab) {
      case 'ai-assistant':
        return (
          <div className="content">
            <h2>How We Leverage AI Technology</h2>
            <p>
              Our platform integrates advanced AI capabilities through OpenAI's API, enabling intelligent document processing and conversational interactions.
            </p>
            <div className="features">
              <div className="feature">
                <h3>Document Processing</h3>
                <p>Advanced file handling with vector store integration for intelligent document analysis and retrieval.</p>
              </div>
              <div className="feature">
                <h3>Thread Management</h3>
                <p>Organized conversation threads with OpenAI integration for context-aware responses.</p>
              </div>
              <div className="feature">
                <h3>Multi-Format Support</h3>
                <p>Support for various file types with automatic processing and vector storage.</p>
              </div>
              <div className="feature">
                <h3>Context Awareness</h3>
                <p>Maintains conversation history and file context for more relevant interactions.</p>
              </div>
            </div>
          </div>
        );
      case 'technology-stack':
        return (
          <div className="content">
            <h2>Technology Stack</h2>
            <p>Details about the platform's underlying technology stack will go here.</p>
          </div>
        );
      case 'security-features':
        return (
          <div className="content">
            <h2>Security Features</h2>
            <p>Details about security features will go here.</p>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="accordion-container">
      <div className="tabs">
        <button
          className={activeTab === 'ai-assistant' ? 'active' : ''}
          onClick={() => setActiveTab('ai-assistant')}
        >
          AI Assistant Integration
        </button>
        <button
          className={activeTab === 'technology-stack' ? 'active' : ''}
          onClick={() => setActiveTab('technology-stack')}
        >
          Technology Stack
        </button>
        <button
          className={activeTab === 'security-features' ? 'active' : ''}
          onClick={() => setActiveTab('security-features')}
        >
          Security Features
        </button>
      </div>
      <div className="tab-content">{renderContent()}</div>
    </div>
  );
};

export default Accordion;
