import React, { useEffect } from 'react';

function Header() {
  useEffect(() => {
    const botao = document.getElementsByClassName("botao")[0];

    const toggleDarkMode = () => {
      const title = document.getElementsByClassName("title")[0];
      const footer = document.getElementsByTagName("footer")[0];

      if (botao.textContent === "Dark mode OFF") {
        botao.textContent = "Dark mode ON";
        document.body.style.backgroundColor = '#000';
        botao.style.backgroundColor = '#fff';
        botao.style.color = '#000';
        title.style.color = '#fff';
        footer.style.backgroundColor = '#333';
        footer.style.color = '#fff';
      } else {
        botao.textContent = "Dark mode OFF";
        document.body.style.backgroundColor = '#fff';
        botao.style.backgroundColor = '#000';
        botao.style.color = '#fff';
        title.style.color = '#000';
        footer.style.backgroundColor = '#f8f9fa';
        footer.style.color = '#000';
      }
    };

    botao.addEventListener("click", toggleDarkMode);

    botao.addEventListener("mouseover", () => {
      botao.style.backgroundColor = '#a9b9c1';
      botao.style.color = '#000';
    });

    botao.addEventListener("mouseout", () => {
      if (botao.textContent === "Dark mode OFF") {
        botao.style.backgroundColor = '#000';
        botao.style.color = '#fff';
      } else {
        botao.style.backgroundColor = '#fff';
        botao.style.color = '#000';
      }
    });

    return () => {
      botao.removeEventListener("click", toggleDarkMode);
    };
  }, []);

  return (
    <header className="bg-blue-600 text-white p-4 flex justify-between items-center">
      <button className="botao bg-black text-white p-2 rounded">Dark mode OFF</button>
      <h1 className="title">Welcome to Dylan's Website</h1>
    </header>
  );
}

export default Header;
