import React from 'react';
import { useEffect, useState } from 'react';
import { Users } from 'lucide-react';

function ContactForm() {
    const [isMobile, setIsMobile] = useState(false);

    useEffect(() => { 
        const checkScreenSize = () => {
            setIsMobile(window.innerWidth < 768);
        };

        checkScreenSize();
        window.addEventListener('resize', checkScreenSize);

        return () => {
            window.removeEventListener('resize', checkScreenSize);
        };

    }, []);

    const handleSubmit = async (event) => {
        event.preventDefault();

        const payload = {
          to: 'dylan.gray@revenuepathgroup.com',
          from: 'dylangray67890@gmail.com',
          subject: 'New message from your website!',
          text: `User Submission: ${firstName}\n${lastName}\n${message}`,
          html: `<p>User Submission:</p>
                 <p>${firstName}</p>
                 <p>${lastName}</p>
                 <p>${message}</p>`,
        };

        try {
          const response = await fetch ('/api', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(payload),
          });

          if (response.ok) {
            alert('Message sent successfully!');
          } else {
            alert('Message failed to send. Please try again later.');
          }

        } catch (error) {
          console.error('Error:', error);
          alert('Message failed to send. Please try again later.');
        }
    };

    const button = document.getElementById('btn');
    button.addEventListener('click', handleSubmit);

    if (button) {
        alert('success');
    } else {
        alert('fail');
    }

    return (
        <div className="flex justify-center">
            <div className="max-w-6xl p-4 w-full">
                <form className="mx-auto" style={{ margin: "150px 0 50px 0", maxWidth: "600px", width: "100%" }}>
                    <h2 className="text-2xl font-semibold text-left mb-5">
                        Let's Connect and Collaborate! {!isMobile && <Users size={24} className="ml-2 inline-block" />}
                    </h2>
                    <div className="relative z-0 w-full mb-5 group">
                        <input value={firstName} type="text" name="floating_first_name" id="floating_first_name" className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-white dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer" placeholder=" " required />
                        <label htmlFor="floating_first_name" className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 rtl:peer-focus:translate-x-1/4 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">First name</label>
                    </div>
                    <div className="relative z-0 w-full mb-5 group">
                        <input value={lastName} type="email" name="floating_email" id="floating_email" className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-white dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer" placeholder=" " required />
                        <label htmlFor="floating_email" className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 rtl:peer-focus:translate-x-1/4 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Email address</label>
                    </div>
                    <div className="relative z-0 w-full mb-5 group">
                        <textarea value={message} name="floating_reason" id="floating_reason" className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-white dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer" placeholder=" " required></textarea>
                        <label htmlFor="floating_reason" className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:start-0 rtl:peer-focus:translate-x-1/4 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6">Reason for contact</label>
                    </div>
                    <button id="btn" type="submit" className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm w-full sm:w-auto px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Submit</button>
                </form>
            </div>
        </div>
    );
}

export default ContactForm;
